INSERT INTO `dbtw_options` VALUES("124", "_transient_dash_4077549d03da2e451c8b5f002294ff51", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/10/join-us-again-for-global-wordpress-translation-day/\'>Join Us Again for Global WordPress Translation Day</a> <span class=\"rss-date\">October 14, 2016</span><div class=\"rssSummary\">The WordPress Polyglots team is organizing the second Global WordPress Translation Day on November 12th. Everyone is invited to join – from anywhere in the world! Translating is one of the easiest ways to get involved with WordPress and contribute to the project. Global WordPress Translation Day is your chance to learn more about translating WordPress, meet [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://heropress.com/essays/wordpress-set-free/\'>HeroPress: WordPress Set Me Free</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/why-pixelgrade-is-experimenting-with-a-225-wordpress-theme-on-themeforest\'>WPTavern: Why PixelGrade is Experimenting with a $225 WordPress Theme on Themeforest</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wp-rest-api-content-endpoints-conditionally-approved-for-merge-in-4-7\'>WPTavern: WP REST API Content Endpoints Conditionally Approved for Merge in 4.7</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/google-analytics-dashboard-for-wp/\' class=\'dashboard-news-plugin-link\'>Google Analytics Dashboard for WP</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=google-analytics-dashboard-for-wp&amp;_wpnonce=ab4a46b536&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Google Analytics Dashboard for WP\'>Install</a>)</span></li></ul></div>", "no");
