INSERT INTO `wp_options` VALUES("755", "_transient_dash_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/11/wordpress-4-7-release-candidate/\'>WordPress 4.7 Release Candidate</a> <span class=\"rss-date\">November 24, 2016</span><div class=\"rssSummary\">The release candidate for WordPress 4.7 is now available. RC means we think we’re done, but with millions of users and thousands of plugins and themes, it’s possible we’ve missed something. We hope to ship WordPress 4.7 on Tuesday, December 6, but we need your help to get there. If you haven’t tested 4.7 yet, [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/elizabeth-shilling-awarded-the-kim-parsell-memorial-scholarship\'>WPTavern: Elizabeth Shilling Awarded the Kim Parsell Memorial Scholarship</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/pdf-image-previews-among-the-improvements-to-media-in-wordpress-4-7\'>WPTavern: PDF Image Previews Among the Improvements to Media in WordPress 4.7</a></li><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/11/wordpress-4-7-release-candidate/\'>Dev Blog: WordPress 4.7 Release Candidate</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> Page Builder by SiteOrigin&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=siteorigin-panels&amp;_wpnonce=e22c5e46b4&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install Page Builder by SiteOrigin\">(Install)</a></li></ul></div>", "no");
