INSERT INTO `wp_postmeta` VALUES("218", "37", "_what_is_wordpress_title", "field_580def4303966");
INSERT INTO `wp_postmeta` VALUES("219", "37", "what_is_wordpress_description", "Wordpress is a leading Content Management System that is used by many companies, sites, blogs.");
INSERT INTO `wp_postmeta` VALUES("220", "37", "_what_is_wordpress_description", "field_580def9a03967");
INSERT INTO `wp_postmeta` VALUES("221", "37", "no_coding_title", "No coding at all!");
INSERT INTO `wp_postmeta` VALUES("222", "37", "_no_coding_title", "field_580deffb03968");
INSERT INTO `wp_postmeta` VALUES("223", "37", "no_coding_description", "With Wordpress anyone without coding or programming at all can make changes to his site, add content, themes, updates and in other words, anything an administrator could do.");
INSERT INTO `wp_postmeta` VALUES("224", "37", "_no_coding_description", "field_580df05903969");
INSERT INTO `wp_postmeta` VALUES("225", "37", "enroll_to_test_it_now_title", "Enroll above to TEST IT NOW!");
