INSERT INTO `wp_postmeta` VALUES("227", "37", "enroll_to_test_it_description", "If you can\'t imagine what I mean you should test it.");
INSERT INTO `wp_postmeta` VALUES("228", "37", "_enroll_to_test_it_description", "field_580df0980396a");
INSERT INTO `wp_postmeta` VALUES("229", "37", "who_benefits_title", "Who benefits from Wordpress?");
INSERT INTO `wp_postmeta` VALUES("230", "37", "_who_benefits_title", "field_580e07d42c092");
INSERT INTO `wp_postmeta` VALUES("231", "37", "who_benefits_body", "<h3>Companies</h3>\r\nAny company can use it if they want to update their sites content simple and fast. From Fortune, BBC to small local shops.\r\n<h3>Personal/portfolio sites</h3>\r\n<p class=\"align-center\">A site that doesn\'t get updates and new content becomes boring and less visited over time. Even for your personal, portfolio site you need to update it so you can attract new visitors and employers.</p>\r\n\r\n<h3>Bloggers</h3>\r\nWordpress started as a blog platform so people could make blogs without coding. It\'s so powerful though that\'s used by any kind of sites nowdays.\r\n<h3>No extra money for you to spend</h3>\r\nSince you will be able to update your site, you won\'t need to pay developers for each change.");
INSERT INTO `wp_postmeta` VALUES("232", "37", "_who_benefits_body", "field_580e08372c093");
INSERT INTO `wp_postmeta` VALUES("233", "28", "who_benefits_title", "Who benefits from Wordpress?");
