INSERT INTO `wp_postmeta` VALUES("624", "95", "what_is_wordpress_title", "What is Wordpress?");
INSERT INTO `wp_postmeta` VALUES("625", "95", "_what_is_wordpress_title", "field_580def4303966");
INSERT INTO `wp_postmeta` VALUES("626", "95", "what_is_wordpress_description", "Wordpress is a leading Content Management System that is used by many companies, sites, blogs.");
INSERT INTO `wp_postmeta` VALUES("627", "95", "_what_is_wordpress_description", "field_580def9a03967");
INSERT INTO `wp_postmeta` VALUES("628", "95", "no_coding_title", "No coding at all!");
INSERT INTO `wp_postmeta` VALUES("629", "95", "_no_coding_title", "field_580deffb03968");
INSERT INTO `wp_postmeta` VALUES("630", "95", "no_coding_description", "With Wordpress anyone without coding or programming at all can make changes to his site, add content, themes, updates and in other words, anything an administrator could do.");
INSERT INTO `wp_postmeta` VALUES("631", "95", "_no_coding_description", "field_580df05903969");
INSERT INTO `wp_postmeta` VALUES("632", "95", "enroll_to_test_it_now_title", "Enroll above to TEST IT NOW!");
INSERT INTO `wp_postmeta` VALUES("633", "95", "_enroll_to_test_it_now_title", "field_580df09a0396b");
INSERT INTO `wp_postmeta` VALUES("634", "95", "enroll_to_test_it_description", "If you can\'t imagine what I mean you should test it.");
INSERT INTO `wp_postmeta` VALUES("635", "95", "_enroll_to_test_it_description", "field_580df0980396a");
INSERT INTO `wp_postmeta` VALUES("636", "95", "who_benefits_title", "Who benefits from Wordpress?");
INSERT INTO `wp_postmeta` VALUES("637", "95", "_who_benefits_title", "field_580e07d42c092");
INSERT INTO `wp_postmeta` VALUES("638", "95", "who_benefits_body", "<h3>Companies</h3>\r\nAny company can use it if they want to update their sites content simple and fast. From Fortune, BBC to small local shops.\r\n<h3>Personal/portfolio sites</h3>\r\n<p class=\"align-center\">A site that doesn\'t get updates and new content becomes boring and less visited over time. Even for your personal, portfolio site you need to update it so you can attract new visitors and employers.</p>\r\n\r\n<h3>Bloggers</h3>\r\nWordpress started as a blog platform so people could make blogs without coding. It\'s so powerful though that\'s used by any kind of sites nowdays.\r\n<h3>No extra money for you to spend</h3>\r\nSince you will be able to update your site, you won\'t need to pay developers for each change.");
INSERT INTO `wp_postmeta` VALUES("639", "95", "_who_benefits_body", "field_580e08372c093");
