INSERT INTO `wp_postmeta` VALUES("1025", "138", "what_is_wordpress_title", "About Wordpress");
INSERT INTO `wp_postmeta` VALUES("1026", "138", "_what_is_wordpress_title", "field_580def4303966");
INSERT INTO `wp_postmeta` VALUES("1027", "138", "what_is_wordpress_description", "Wordpress is a leading Content Management System that is used by many companies, sites, blogs.");
INSERT INTO `wp_postmeta` VALUES("1028", "138", "_what_is_wordpress_description", "field_580def9a03967");
INSERT INTO `wp_postmeta` VALUES("1029", "138", "no_coding_title", "No coding at all!");
INSERT INTO `wp_postmeta` VALUES("1030", "138", "_no_coding_title", "field_580deffb03968");
INSERT INTO `wp_postmeta` VALUES("1031", "138", "no_coding_description", "With Wordpress anyone without coding or programming at all can make changes to his site, add content, themes, updates and in other words, anything an administrator could do.");
INSERT INTO `wp_postmeta` VALUES("1032", "138", "_no_coding_description", "field_580df05903969");
INSERT INTO `wp_postmeta` VALUES("1033", "138", "enroll_to_test_it_now_title", "Enroll above to TEST IT NOW!");
INSERT INTO `wp_postmeta` VALUES("1034", "138", "_enroll_to_test_it_now_title", "field_580df09a0396b");
INSERT INTO `wp_postmeta` VALUES("1035", "138", "enroll_to_test_it_description", "If you can\'t imagine what I mean you should test it.");
INSERT INTO `wp_postmeta` VALUES("1036", "138", "_enroll_to_test_it_description", "field_580df0980396a");
INSERT INTO `wp_postmeta` VALUES("1037", "138", "who_benefits_title", "Who benefits from Wordpress?");
INSERT INTO `wp_postmeta` VALUES("1038", "138", "_who_benefits_title", "field_580e07d42c092");
INSERT INTO `wp_postmeta` VALUES("1039", "138", "who_benefits_body", "<h3>Companies</h3>\r\nAny company can use it if they want to update their sites content simple and fast. From Fortune, BBC to small local shops.\r\n<h3>Personal/portfolio sites</h3>\r\n<p class=\"align-center\">A site that doesn\'t get updates and new content becomes boring and less visited over time. Even for your personal, portfolio site you need to update it so you can attract new visitors and employers.</p>\r\n\r\n<h3>Bloggers</h3>\r\nWordpress started as a blog platform so people could make blogs without coding. It\'s so powerful though that\'s used by any kind of sites nowdays.\r\n<h3>Value For MONEY</h3>\r\nSince you will be able to update your site, you won\'t need to pay developers for each change.");
INSERT INTO `wp_postmeta` VALUES("1040", "138", "_who_benefits_body", "field_580e08372c093");
