INSERT INTO `wp_postmeta` VALUES("1259", "190", "what_is_wordpress_title", "About Wordpress");
INSERT INTO `wp_postmeta` VALUES("1260", "190", "_what_is_wordpress_title", "field_580def4303966");
INSERT INTO `wp_postmeta` VALUES("1261", "190", "what_is_wordpress_description", "Wordpress is a leading Content Management System that is used by many companies, sites, blogs.");
INSERT INTO `wp_postmeta` VALUES("1262", "190", "_what_is_wordpress_description", "field_580def9a03967");
INSERT INTO `wp_postmeta` VALUES("1263", "190", "no_coding_title", "No coding at all!");
INSERT INTO `wp_postmeta` VALUES("1264", "190", "_no_coding_title", "field_580deffb03968");
INSERT INTO `wp_postmeta` VALUES("1265", "190", "no_coding_description", "With Wordpress anyone without coding or programming at all can make changes to his site, add content, themes, updates and in other words, anything an administrator could do.");
INSERT INTO `wp_postmeta` VALUES("1266", "190", "_no_coding_description", "field_580df05903969");
INSERT INTO `wp_postmeta` VALUES("1267", "190", "enroll_to_test_it_now_title", "Enroll above to TEST IT NOW!");
INSERT INTO `wp_postmeta` VALUES("1268", "190", "_enroll_to_test_it_now_title", "field_580df09a0396b");
INSERT INTO `wp_postmeta` VALUES("1269", "190", "enroll_to_test_it_description", "If you can\'t imagine what I mean you should test it.");
INSERT INTO `wp_postmeta` VALUES("1270", "190", "_enroll_to_test_it_description", "field_580df0980396a");
INSERT INTO `wp_postmeta` VALUES("1271", "190", "who_benefits_title", "Who benefits from Wordpress?");
INSERT INTO `wp_postmeta` VALUES("1272", "190", "_who_benefits_title", "field_580e07d42c092");
INSERT INTO `wp_postmeta` VALUES("1273", "190", "who_benefits_body", "<h3>Companies</h3>\r\nAny company can use it if they want to update their sites content simple and fast. From Fortune, BBC to small local shops.\r\n<h3>Personal/portfolio sites</h3>\r\n<p class=\"align-center\">A site that doesn\'t get updates and new content becomes boring and less visited over time. Even for your personal, portfolio site you need to update it so you can attract new visitors and employers.</p>\r\n\r\n<h3>Bloggers</h3>\r\nWordpress started as a blog platform so people could make blogs without coding. It\'s so powerful though that\'s used by any kind of sites nowdays.\r\n<h3>Value For MONEY</h3>\r\nSince you will be able to update your site, you won\'t need to pay developers for each change.");
INSERT INTO `wp_postmeta` VALUES("1274", "190", "_who_benefits_body", "field_580e08372c093");
