INSERT INTO `wp_postmeta` VALUES("1514", "238", "no_coding_title", "No coding at all!");
INSERT INTO `wp_postmeta` VALUES("1515", "238", "_no_coding_title", "field_580deffb03968");
INSERT INTO `wp_postmeta` VALUES("1516", "238", "no_coding_description", "With Wordpress anyone without coding or programming at all can make changes to his site, add content, themes, updates and in other words, anything an administrator could do.");
INSERT INTO `wp_postmeta` VALUES("1517", "238", "_no_coding_description", "field_580df05903969");
INSERT INTO `wp_postmeta` VALUES("1518", "238", "enroll_to_test_it_now_title", "Controlled by you but with customer support!");
INSERT INTO `wp_postmeta` VALUES("1519", "238", "_enroll_to_test_it_now_title", "field_580df09a0396b");
INSERT INTO `wp_postmeta` VALUES("1520", "238", "enroll_to_test_it_description", "You will control your site and you we will have customer support since I\'m not leaving my clients when needed.");
INSERT INTO `wp_postmeta` VALUES("1521", "238", "_enroll_to_test_it_description", "field_580df0980396a");
INSERT INTO `wp_postmeta` VALUES("1522", "238", "who_benefits_title", "Who benefits from Wordpress?");
INSERT INTO `wp_postmeta` VALUES("1523", "238", "_who_benefits_title", "field_580e07d42c092");
INSERT INTO `wp_postmeta` VALUES("1524", "238", "who_benefits_body", "<h3>Companies</h3>\r\nAny company can use it if they want to update their sites content simple and fast. From Fortune, BBC to small local shops.\r\n<h3>Personal/portfolio sites</h3>\r\n<p class=\"align-center\">A site that doesn\'t get updates and new content becomes boring and less visited over time. Even for your personal, portfolio site you need to update it so you can attract new visitors and employers.</p>\r\n\r\n<h3>Bloggers</h3>\r\nWordpress started as a blog platform so people could make blogs without coding. It\'s so powerful though that\'s used by any kind of sites nowdays.\r\n<h3>Value For MONEY</h3>\r\nSince you will be able to update your site, you won\'t need to pay developers for each change.");
