INSERT INTO `wp_posts` VALUES("229", "1", "2016-11-28 15:28:38", "2016-11-28 15:28:38", "I\' m running all my sites in cloud VPS with Digital Ocean. At first months I had time to manage my servers. Now that I\'m working in some projects I don\'t have time for this so I gave the \"keys\" to Cloudways to do it for me and they worth every penny. Amazing support, great API that handles with simple clicks most jobs. Really pleased from them!", "Cloudways", "", "publish", "closed", "closed", "", "cloudways", "", "", "2016-11-28 15:28:38", "2016-11-28 15:28:38", "", "0", "http://localhost/TestWordpress/?post_type=resources2&#038;p=229", "0", "resources2", "", "0");
