INSERT INTO `wp_posts` VALUES("234", "1", "2016-11-28 15:44:19", "2016-11-28 15:44:19", "<div class=\"row\">\r\n        <div class=\"col-sm-6\">\r\n            <div class=\"form-group\">\r\n                <label class=\"sr-only\" for=\"contact-name\">Name</label>\r\n                [text* your-name class:form-control class:input-lg id:contact-name placeholder \"Your name\"]\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"col-sm-6\">\r\n            <div class=\"form-group\">\r\n                <label class=\"sr-only\" for=\"contact-email\">Email</label>\r\n                [email* your-email class:form-control class:input-lg id:contact-email placeholder \"Your email\"]\r\n            </div>\r\n        </div>\r\n        <div class=\"col-sm-12\">\r\n        <div class=\"form-group\">\r\n            <label class=\"sr-only\" for=\"contact-subject\">Subject</label>\r\n            [text your-subject class:form-control class:input-lg id:contact-subject placeholder \"Your subject\"]\r\n        </div>\r\n        </div>\r\n\r\n        <div class=\"col-sm-12\">\r\n            <div class=\"form-group\">\r\n                <label class=\"sr-only\" for=\"contact-words\">Message</label>\r\n                [textarea your-message class:form-control class:input-lg id:contact-words x3 placeholder \"Your message\"]\r\n            </div>\r\n        </div>\r\n</div>\r\n[submit class:btn class:btn-info class:btn-lg class:pull-right \"Message me now!\"]\nMy Wordpress \"[your-subject]\"\n[your-name] <george@besmartbesimple.com>\nFrom: [your-name] <[your-email]>\r\nSubject: [your-subject]\r\n\r\nMessage Body:\r\n[your-message]\r\n\r\n--\r\nThis e-mail was sent from a contact form on My Wordpress (http://localhost/TestWordpress)\ngeorge@besmartbesimple.com\nReply-To: [your-email]\n\n\n\n\nMy Wordpress \"[your-subject]\"\nMy Wordpress <george@besmartbesimple.com>\nMessage Body:\r\n[your-message]\r\n\r\n--\r\nThis e-mail was sent from a contact form on My Wordpress (http://localhost/TestWordpress)\n[your-email]\nReply-To: george@besmartbesimple.com\n\n\n\nThank you for your message. It has been sent.\nThere was an error trying to send your message. Please try again later.\nOne or more fields have an error. Please check and try again.\nThere was an error trying to send your message. Please try again later.\nYou must accept the terms and conditions before sending your message.\nThe field is required.\nThe field is too long.\nThe field is too short.\nThe date format is incorrect.\nThe date is before the earliest one allowed.\nThe date is after the latest one allowed.\nThere was an unknown error uploading the file.\nYou are not allowed to upload files of this type.\nThe file is too big.\nThere was an error uploading the file.\nThe number format is invalid.\nThe number is smaller than the minimum allowed.\nThe number is larger than the maximum allowed.\nThe answer to the quiz is incorrect.\nYour entered code is incorrect.\nThe e-mail address entered is invalid.\nThe URL is invalid.\nThe telephone number is invalid.", "Contact form", "", "publish", "closed", "closed", "", "contact-form", "", "", "2016-11-28 15:44:19", "2016-11-28 15:44:19", "", "0", "http://localhost/TestWordpress/?post_type=wpcf7_contact_form&p=234", "0", "wpcf7_contact_form", "", "0");
